import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setColor, setCategoryName } from './actions/AppAction';
import './App.css';

import Card from './components/Card';
import CategoryEdit from './components/CategoryEdit';

const App = () => {
  const dispatch = useDispatch();
  const data = useSelector(state => state.app.data);
  const categories = useSelector(state => state.app.categories);

  const changeColor = (ev) => {
    dispatch(setColor(ev.target.value));
  }
  const changeName = (ev) => {
    dispatch(setCategoryName(ev.target.value));
  }

  return (
    <div className="App">
      <Card card={data} categories={categories} />
      <CategoryEdit changeName={changeName} changeColor={changeColor} categoryDetails={categories[0]} />
    </div>
  )
}
export default App;
