const initialState = {
    data: {
        imgSrc: './images/landscape_207075.jpg',
        category: 'אוכל',
        title: 'סיור קולינרי  בין המסעדות הערביות הכי טובות בחיפה',
        subTitle: 'גם אני, שעסוק כל השבוע, מתפנה לצפות בטלוויזיה רק בסופי השבוע, ונוכחתי שרוב תוכניות הטלוויזיה בכל הערוצים הן תוכניות אוכל.',
        author: 'שירית גל'
    },
    categories:
        [{
            category: 'אוכל',
            color: 'red'
        }]
}
export default function AppReducer(state = initialState, action) {
    console.log("action.color:", action.color)
    switch (action.type) {
        case 'SET_COLOR': {
            let idx = state.categories.findIndex(item => state.data.category === state.categories[0].category)
            let newCategories = [...state.categories]
            newCategories[idx].color = action.color;
            return {
                ...state,
                categories: newCategories
            }
        }
        case 'SET_CATEGORY_NAME': {
            let idx = state.categories.findIndex(item => state.data.category === state.categories[0].category)
            let newCategories = [...state.categories]
            newCategories[idx].category = action.newName;
            return {
                ...state,
                categories: newCategories,
                data: {
                    ...state.data,
                    category: action.newName
                }
            }
        }
        default:
            return state;
    }

};
