const initialState = {
    data: {
        imgSrc: './images/landscape_207075.jpg',
        category: 'אוכל',
        title: 'סיור קולינרי  בין המסעדות הערביות הכי טובות בחיפה',
        subTitle: 'גם אני, שעסוק כל השבוע, מתפנה לצפות בטלוויזיה רק בסופי השבוע, ונוכחתי שרוב תוכניות הטלוויזיה בכל הערוצים הן תוכניות אוכל.',
        author: 'שירית גל'
    },
    categories:
        [{
            category: 'אוכל',
            color: 'red'
        }]
}
export default function AppReducer(state = initialState, action) {
    switch (action.type) {
        case 'SET_COLOR': {
            let currColor = categories[0].color;
            return {
                ...state,
                categories: state.categories.map(item => {
                    if (data.category === categories[0].category && categories[0].color !== action.color) item.color = action.color
                })
            }
        }
        default:
            return state;
    }

};
