export function setColor(color) {
    AppService.toggleTheme();
    return async dispatch => {
        dispatch({ type: 'TOGGLE_THEME' })
    }
}
export function setCategoryName(newName) {
    const theme = AppService.loadTheme();
    return async dispatch => {
        dispatch({ type: 'SET_THEME', theme })
    }
}
