import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setColor, setCategoryName } from './actions/AppAction';
import './App.css';

import Card from './components/Card';
import CategoryEdit from './components/CategoryEdit';

const App = () => {
  const data = useSelector(state => state.app.data);
  const categories = useSelector(state => state.app.categories);

  const changeColor = (ev) => {
    console.log(ev.target.value)
    useDispatch(setColor(ev.target.value));
  }
  const changeName = (ev) => {
    console.log(ev.target.value);
    useDispatch(setCategoryName(ev.target.value));
  }

  return (
    <div className="App">
      <Card card={data} />
      <CategoryEdit changeName={changeName} changeColor={changeColor} categoryDetails={categories[0]} />
    </div>
  )
}
export default App;

